var appCanComments=appCanComments||{buttons:{}};$(function(){var app=appCanComments;app.buttons.reply=function(){var that=$(this);$('#comment-form #comment-thread').val(that.attr('data-comment-thread'));var comment=that.closest('li.comment');var content=comment.find('.content');var answer=$('.leave-comment .answer');answer.find('.apponent').html(content.html());$('.leave-comment .reply-heading').hide();$('.leave-comment .answer').show()}
$('#comments .reply-btn').on('click',app.buttons.reply);app.buttons.loadMore=function(){var that=$(this);var btn=that;if(btn.attr('disabled')==='disabled'){return!1}
$.ajax({url:that.data('href'),data:{slug:that.data('slug'),offset:that.data('offset')},async:!1,success:function(response){if(response==!1){btn.attr('disabled','disabled')}else{$('.comment-section .comment-list').append(response);btn.data('offset',Number(btn.data('offset'))+Number(btn.data('step')))}},error:function(e){btn.attr('disabled','disabled')}});$('#comments .reply-btn').on('click',app.buttons.reply);return!1};$('#comments .load-more-btn').on('click',app.buttons.loadMore);appCanComments=app})
